document.addEventListener("DOMContentLoaded",function(){const e=document.querySelectorAll("a"),t=window.location.hostname;e.forEach(e=>{const n=e.getAttribute("href");if(!n||n.startsWith("#"))return;if(n.startsWith("http://")||n.startsWith("https://"))try{const s=new URL(n).hostname;s!==t&&(e.setAttribute("target","_blank"),e.setAttribute("rel","noopener noreferrer"))}catch{}else if(n.startsWith("mailto:"))return})})